import React from 'react'
import {makeStyles} from '@material-ui/core/styles'
import Grid from '@material-ui/core/Grid'
import Photo from '../Images/Edgar.jpg'
import AppBar from '../AppBar/AppBar';

const useStyles = makeStyles((theme) => ({
  root:{
    flexGrow: 1,
  },
  backUp:{
    padding: theme.spacing(1),
    textAlign: 'center',
    color: 'red',
    backgroundColor: '#52B5F2' /* 'rgb(49, 208, 129)' */,
    height: '40vh'
  }, 
  photo:{
    marginLeft: '3rem',
    width: '20%',
    display: 'block',
    boxShadow: '2px 2px 2px 1px rgba(0, 0, 0, 0.2)'
  },
  imgContainer:{
    marginTop: '10%',
    display: 'flex',
    justifyContent: 'flex-start',
    height: '50vh',
    boxShadow: '2px 0px 0px 0px rgba(0, 0, 0, 0.2)'

  }
}));

export default function GridContainer(){
  const classes = useStyles()

  return(

      <Grid item xs = {12} className = {classes.backUp}>
        <Grid item xs = {11} className = {classes.imgContainer}>
          <img src = {Photo} className = {classes.photo} />
        </Grid>
      </Grid>
  )
}
