import React from 'react'
import {makeStyles} from '@material-ui/core/styles'
import Grid from '@material-ui/core/Grid'
import Photo from '../Images/EdgarFranks1.jpeg'

const useStyles = makeStyles((theme) => ({
  root:{
    flexGrow: 1
  },
  backUp:{
    padding: theme.spacing(1),
    textAlign: 'left',
    backgroundColor: '#52B5F2' /* 'rgb(49, 208, 129)' */,
    height: '40vh',
    gridTemplateColumns: 'repeat(auto-fill, minmax(15rem, 1fr))',

  }, 
  photo:{
    marginLeft: '3rem',
    width: '12rem',
    display: 'block',
    boxShadow: '2px 2px 2px 1px rgba(0, 0, 0, 0.2)',
    [theme.breakpoints.down('sm')]: {
      width:'8rem',
    }
  },
  imgContainer:{
    marginTop: '10%',
    display: 'flex',
    justifyContent: 'flex-start',
    height: '50vh'
  }
}));

export default function GridContainer(){
  const classes = useStyles()

  return(
      <Grid item xs = {12}  className = {classes.backUp}>
        <Grid item xs = {11} className = {classes.imgContainer}>
          <img src = {Photo} className = {classes.photo} alt = 'MyImage'/>
        </Grid>
      </Grid>
  )
}
